import React, { useState } from "react";
import Checkbox from "../Checkbox/Checkbox";
import Card from "../../Card/Card";

const ListItem = (props) => {
  const [isCheckedAll, setIsCheckedAll] = useState(false);

  const handleCheckTrue = (checkboxData) => {
    const checkData = {
      ...checkboxData,
      id: Math.random().toString(),
    };
    props.onCheckTrue(checkData);
  };

  const handleCheckAllChange = (checked) => {
    setIsCheckedAll(checked);
  };

  // Function to handle the "All" checkbox change
  const handleAllCheckboxChange = () => {
    setIsCheckedAll(!isCheckedAll);
    handleCheckAllChange(!isCheckedAll); // Update the state of all checkboxes
  };

  return (
    <Card className="container">
      <Checkbox
        rarity="All"
        hex=""
        isChecked={isCheckedAll}
        onClickTrue={handleCheckTrue}
        onCheckAllChange={handleCheckAllChange}
      />
      <Checkbox
        rarity={props.rarity}
        hex={props.hex}
        isChecked={isCheckedAll}
        onClickTrue={handleCheckTrue}
        onCheckAllChange={handleCheckAllChange}
      />
    </Card>
  );
};

export default ListItem;
