import React, { useState, useEffect } from 'react';
import './Checkbox.css';

const Checkbox = (props) => {
  const { isChecked, rarity, hex, onClickTrue, onCheckAllChange } = props;

  const [isCheckedInternal, setIsCheckedInternal] = useState(isChecked);

  useEffect(() => {
    setIsCheckedInternal(isChecked);
  }, [isChecked]);

  const handleChange = (event) => {
    const { checked } = event.target;
    setIsCheckedInternal(checked);

    if (onClickTrue) {
      const rarityContent = { rarity, hex };
      onClickTrue(rarityContent);
    }

    if (onCheckAllChange) {
      onCheckAllChange(checked);
    }
  };

  return (
    <div>
      <label className="checkbox-container">
        <input type="checkbox" checked={isCheckedInternal} onChange={handleChange} />
        <span className="checkmark"></span>
      </label>
      <div className="raraty">{rarity}</div>
      <div hidden={true}>{hex}</div>
    </div>
  );
};

export default Checkbox;
