import React,{useState,useEffect} from 'react';
import './Checkbox.css';

const Checkbox = ({data,onCheckboxTrue}) => {

  const [isChecked, setIsChecked] = useState(false);
  const [rarityCh,setRaraty] = useState('');
  const [hexCh,setHexCode] = useState('');

  useEffect(() => {
    if(data){
      console.log(data);
      setRaraty(data.raraty);
      setHexCode(data.hex);

      //console.log("rarityCH: "+rarityCh);
      //console.log("hexCH: "+hexCh);
    }
  },[data]);

  const handleChange = (event) => {
    setIsChecked(!isChecked);
    if(!isChecked==true)
    {
      console.log('Checkbox True');
      const rarityContent = {rarity:rarityCh,hex:hexCh}
    }
   
  };

  return (
    <div>
    <label className="checkbox-container">
      <input type="checkbox" checked={isChecked} onChange={handleChange} />
      <span className="checkmark"></span>
    </label>
    <div className='raraty'>{rarityCh}</div>
    <div hidden={true}>{hexCh}</div>
    </div>
  );
};

export default Checkbox;
