import React,{useState} from 'react';
import './Checkbox.css';

const Checkbox = (props) => {


  const [isChecked, setIsChecked] = useState(false);
  const [rarityCh,setRaraty] = useState(props.raraty);
  const [hexCh,setHexCode] = useState(props.hex);
  console.log(rarityCh);


  const handleChange = (event) => {
    setIsChecked(!isChecked);
    if(isChecked)
    {
      console.log('Checkbox True');
      const rarityContent = {rarity:rarityCh,hex:hexCh}
    }
   
  };

  return (
    <div>
    <label className="checkbox-container">
      <input type="checkbox" checked={isChecked} onChange={handleChange} />
      <span className="checkmark"></span>
    </label>
    <div className='raraty' value={rarityCh} />
    <div hidden={true} value={hexCh}/>
    </div>
  );
};

export default Checkbox;
