import './ListItem.css'
import Checkbox from '../Checkbox/Checkbox';
import Card from '../../Card/Card';
import react,{useState} from 'react';

const ListItem = (props) => {
  const [checkboxes, setCheckboxes] = useState([]);

  const saveClickTrue = (checkboxData) => {
    const checkData = {
      ...checkboxData,
      id:Math.random().toString()
    };
    props.onCheckTrue(checkData);
  }

  const handleCheckAllChange = (checked) => {
    // Update the state or perform any desired action when "check all" is changed
    // Iterate over the checkboxes and update their individual checked state accordingly
    const updatedCheckboxes = checkboxes.map((checkbox) => {
      return {
        ...checkbox,
        isChecked: checked
      };
    });
  };

  return (
    <Card className="container">
      <Checkbox onClickTrue={saveClickTrue} onCheckAllChange={handleCheckAllChange}/>
      <Checkbox rarity={props.rarity} hex={props.hex} onClickTrue={saveClickTrue}/>
    </Card>
    
  );
};

export default ListItem;
